mapper.select:
select pwd
from Member2
where user_id=#{user_id}

select count(*)
from Member2
where nickname=#{nickname}

select pet_seq, species, kind, pet_name, pet_yy, user_id, gender
from Pet
where user_id=#{user_id}

mapper.update:
update Pet
set species=#{species}, kind=#{kind}, pet_name=#{pet_name}, pet_yy=#{pet_yy}, gender=#{gender}
where user_id=#{user_id}
